mapper.select:
SELECT COUNT(*) FROM "BOARD"
WHERE BOARD_DEL_FL = 'N'
AND BOARD_CODE = #{boardCode}

SELECT * FROM BOARD_IMG
WHERE BOARD_NO = #{boardNo}
ORDER BY IMG_ORDER

SELECT COUNT(*) FROM "BOARD_LIKE"
WHERE BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}

mapper.delete:
DELETE FROM "BOARD_LIKE"
WHERE BOARD_NO = #{boardNo}
AND MEMBER_NO = #{memberNo}

mapper.update:
UPDATE "BOARD" SET
READ_COUNT = READ_COUNT + 1 
WHERE BOARD_NO = #{boardNo}

mapper.insert:
INSERT INTO "BOARD_IMG" SELECT SEQ_IMG_NO.NEXTVAL, A.* FROM (
SELECT #{img.imagePath} IMG_PATH, #{img.imageReName} IMG_RENAME, #{img.imageOriginal} IMG_ORIGINAL, #{img.imageOrder} IMG_ORDER, #{img.boardNo} BOARD_NO FROM DUAL
) A

mapper.update:
UPDATE "BOARD" SET
BOARD_TITLE = #{boardTitle},
BOARD_CONTENT = #{boardContent},
B_UPDATE_DATE = SYSDATE 
WHERE BOARD_CODE = #{boardCode}
AND BOARD_NO = #{boardNo}

mapper.delete:
DELETE FROM "BOARD_IMG"
WHERE BOARD_NO = #{boardNo}
AND IMG_ORDER IN ( ${deleteList} )

mapper.update:
UPDATE "BOARD_IMG" SET
IMG_PATH = #{imagePath},
IMG_ORIGINAL = #{imageOriginal},
IMG_RENAME = #{imageReName}
WHERE BOARD_NO = #{boardNo}
AND IMG_ORDER = #{imageOrder}

UPDATE "BOARD" SET
BOARD_DEL_FL = 'Y'
WHERE BOARD_CODE = #{boardCode}
AND BOARD_NO = #{boardNo}
